import React, {useState} from "react";
import axios from "axios";
import "./Container.css";

export default function Container() {
  const [finish, setFinish] = useState(false);
  const [temperature, setTemperature] = useState(null);
   function handleResponse(response) {
    console.log(response.data);
    setTemperature(response.data.main.temp);
    setFinish(true);
  }
  if (finish) {
  return (
    <div className="Container">
      <div className="card">
        <div className="card-body">
         <div className="form-group">
        <form>
          <input type="text" placeholder="Enter a city " className="city" />
          <input
            type="submit"
            value="Search"
            className="submitButton"
            autocomplete="off"
          />
          <input type="submit"  value="Current" className="current"/>
        </form>
      </div>
      <h1> Montreal </h1>
      <h2>Last updated: Saturday 17:51</h2>
      <h3>Cloud</h3>
         <div className="degree">
        <img
          src="http://openweathermap.org/img/wn/03n@2x.png"
          alt=""
          className="iconImg"
        />
        <span className="temp">{temperature}</span>
        <span className="unit">
          <a href="/" className="active">
            °C 
          </a> | <a href="/">°F</a>
        </span>
      </div>
      <br />
          <ul>
            <li>Humidity: 74 %</li>
            <li>Wind: 1.5 km/h</li>
          </ul>
        </div>
      </div>
    </div>
  );
  } else { 
  const apiKey = "19f46cb6a372e938ec33a12b4f819dec";
  let city = "New York";
  let apiUrl =`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
  axios.get(apiUrl).then(handleResponse);
  return "The app is loading....";
  }


}
